import PropTypes from "prop-types";
import React from 'react';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts';

/** datas */
import { MockedAPI } from "../hooks/hooksMockedApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";

const DailyActivitiesContainer = styled.div`
  background: ${styleVar.neutral100};
  border-radius: 5px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.0212249);
  height: 320px;
  position: relative;
  width: 835px;
`;


const DailyActivitiesTitle = styled.h2`
  position: absolute;
  top: 1.5rem;
  left: 2rem;

  margin: 0;

  font-size: 1rem;
  font-weight: 500;

  @media (max-width: 1340px) {
    top: 1rem;
    left: 1.5rem;
  }
`;

export function DailyActivities({ userId }) {

    const api = new MockedAPI()
    const dailyActivities = api.getDailyActivitiesById(userId)
    //const averageSession = api.getAverageSessionsById(userId)


    return (
      <DailyActivitiesContainer>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart
            width={500}
            height={300}
            data={dailyActivities}
            margin={{
              top: 5,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="day" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar maxBarSize={7} dataKey="kilogram" fill={`${styleVar.neutral800}`} radius={[50, 50, 0, 0]}/>
            <Bar maxBarSize={7} dataKey="calories" fill={`${styleVar.primary500}`} radius={[50, 50, 0, 0]}/>
          </BarChart>
        </ResponsiveContainer>
      </DailyActivitiesContainer>

      );

  }

