import { useState, useEffect } from 'react'

const BASE_URL = "http://localhost:3000";

export function useFetchApi(userId, service) {
    const [data, setData] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(false);
    const endpoints ={
        "user" : `user/${userId}`, 
        "daily-activity" : `user/${userId}/activity`, 
        "average-sessions": `user/${userId}/average-sessions`,
        "performance" : `user/${userId}/performance`
    }
    let endpoint;
    for(let i in endpoints){
        let value = endpoints[i];
        if(i === service){
            endpoint = value;
        }
    }
    //const endpoint = getEndpoint(userId, service);
    const url = `${BASE_URL}/${endpoint}`;

    useEffect(() => {
        setIsLoading(true);

        fetch(url)
            .then(res =>res.json())
            .then((data) => {
                console.log(data)

                for(let item of data.data.sessions){
                    console.log(item)
                    setData(item, service);
                }
            })
            .catch ((err) =>{
                console.log(err)
                setError(true)
            })
            }, [service, userId, endpoint2])
    return { data, isLoading, error };
}

