import { MockedAPI } from "./useMockedApi";
import { useSportSeeApi } from "./useSportSeeApi";


export function FacadePattern(userId, service) {
    const { data, isLoading, error } = useSportSeeApi("daily-activity", userId );

    let dailyActivities = data;
    if (error || isLoading) {
        const api = new MockedAPI()
        dailyActivities = api.getDailyActivitiesById(userId);
    }
}
