import { useParams } from "react-router";

/** components */
import { Header } from "../components/Header";
import { AsideBar } from "../components/AsideBar";
import { DailyActivities } from "../components/DailyActivities";
import { CaloriesCard } from "../components/CaloriesCard";
import { AverageSessions } from "../components/AverageSessions";
import {Performance} from "../components/Performance";

/** datas */
import { MockedAPI } from "../hooks/useMockedApi";

/** styles */
import "../styles/dashboard.css";
import styled from "styled-components";
import { color } from "../utils/style/styleVariables";

const DashboardContainer = styled.main`
  display: grid;
  grid-template-columns: 7.5rem 1fr;
`;

const MainContent = styled.section`
  padding: 3rem 5rem;

  @media (max-width: 1340px) {
    padding: 1.5rem 2rem;
  }
`;

const MainTitle = styled.h1`
  margin: 0;
  font-size: 3rem;
  font-weight: 500;

  @media (max-width: 1340px) {
    font-size: 2.5rem;
  }
`;

const FirstName = styled.span`
  color: ${color.primary500};
`;

const Message = styled.p`
  margin: 2rem 0 4rem 0;

  font-size: 1.1rem;

  @media (max-width: 1340px) {
    margin: 0.5rem 0 2rem 0;

    font-size: 1.05rem;
  }
`;

const ContentGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 2rem;

  @media (max-width: 1340px) {
    gap: 1rem;
  }
`;

const ChartsGrid = styled.div`
  grid-column: 1/4;

  display: grid;
  grid-template: 20rem 16rem / repeat(3, 1fr);
  gap: 2rem;

  @media (max-width: 1340px) {
    grid-template: 18rem 14rem / repeat(3, 1fr);
    gap: 1rem;
  }

  > * {
    border-radius: 0.25rem;
    overflow: hidden;
  }
`;

const MainChart = styled.div`
  grid-column: 1/4;
`;

export function Dashboard() {
  let { id } = useParams();
  let userId = parseInt(id);
//   const { data, isLoading, error } = new MockedAPI(
//     `user/${userId}`,
//     "firstName"
//   );
const api = new MockedAPI()
const firstNameById = api.getFirstNameById(userId)

//   if (error) {
//     return (
//       <div>
//         <Header />

//         <DashboardContainer>
//           <AsideBar />
//           <MainContent></MainContent>
//         </DashboardContainer>
//       </div>
//     );
//   }

  return (
    <div>
      <Header />

      <DashboardContainer>
        <AsideBar />
        <MainContent>
          <MainTitle>
            Bonjour <FirstName>{firstNameById}</FirstName>
          </MainTitle>
          <Message>
            {firstNameById === "Utilisateur inconnu" ? (
              ""
            ) : (
              <span>
                F√©licitations ! Vous avez explos√© vos objectifs hier
                !&nbsp;üëè
              </span>
            )}
          </Message>

          <ContentGrid>
            <ChartsGrid>
              <MainChart>
                <DailyActivities userId={userId} />
              </MainChart>

              <AverageSessions userId={userId} />

              <Performance userId={userId} />

              {/* <ScoreChart userId={userId} />  */}
            </ChartsGrid>

            <CaloriesCard userId={userId} />
          </ContentGrid>
        </MainContent>
      </DashboardContainer>
    </div>
  );
}
