            })
            .catch ((err) =>{
                console.log(err)
                setError(true)
            })
            }, [])
    return { data, isLoading, error };
}

function getEndpoint(userId, service){
    const endpoints ={
        "user" : `user/${userId}`, 
        "daily-activity" : `user/${userId}/activity`, 
        "average-sessions": `user/${userId}/average-sessions`,
        "performance" : `user/${userId}/performance`
    }
    let endpoint;

    for(let i in endpoints){
        let value = endpoints[i];
        if(i === service){
            endpoint = value;
        }
    }
    return endpoint;
}

function getDailyActivitiesById(userId) {
