import {
    Bar,
    BarChart,
    CartesianGrid,
    ResponsiveContainer,
    Tooltip,
    XAxis,
    YAxis,
  } from "recharts";
/** datas */
import { MockedAPI } from "../hooks/hooksMockedApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";

const DailyActivitiesContainer = styled.div`
  position: relative;
  height: 100%;
  background: ${styleVar.neutral100};
`;

export function DailyActivities({ userId }){

    const api2 = new MockedAPI()
    const dailyActivities = api2.getDailyActivitiesById(userId)

    return(
        <div>
            <DailyActivitiesContainer>
            {/* <LineChart width={400} height={400} data={data}>
                <Line type="monotone" dataKey="uv" stroke="#8884d8" />
            </LineChart> */}
                <ResponsiveContainer width="100%" height="100%">
                    <BarChart
                    data={dailyActivities}
                    margin={{ top: 80, right: 48, bottom: 32, left: 48 }}
                    barGap={8}
                    barCategoryGap="35%"
                    >
                    <CartesianGrid
                        strokeDasharray="3 3"
                        vertical={false}
                        stroke={`${styleVar.neutral200}`}
                    />
                    <XAxis
                        dataKey="day"
                        dy={16}
                        padding={{ left: -48, right: -48 }}
                        stroke={`${styleVar.neutral400}`}
                        tickLine={false}
                        tick={{ fontSize: 14, fontWeight: 500 }}
                    />
                    <YAxis
                        yAxisId="kg"
                        dataKey="kilogram"
                        domain={["dataMin - 1", "dataMax + 2"]}
                        allowDecimals={false}
                        dx={48}
                        orientation="right"
                        stroke={`${styleVar.neutral400}`}
                        axisLine={false}
                        tickLine={false}
                    />
                    <YAxis
                        yAxisId="cal"
                        dataKey="calories"
                        domain={[0, "dataMax + 50"]}
                        hide={true}
                    />
                    <Bar
                        yAxisId="kg"
                        dataKey="kilogram"
                        maxBarSize={8}
                        fill={`${styleVar.neutral800}`}
                        radius={[50, 50, 0, 0]}
                    />
                    <Bar
                        yAxisId="cal"
                        dataKey="calories"
                        maxBarSize={8}
                        fill={`${styleVar.primary500}`}
                        radius={[50, 50, 0, 0]}
                    />
                    <Tooltip
                        content={<CustomTooltip />}
                        cursor={{
                        fill: "rgba(0, 0, 0, 0.1)",
                        }}
                    />
                    </BarChart>
                </ResponsiveContainer>

            </DailyActivitiesContainer>
        </div>
    )
}
