import PropTypes from "prop-types";
/** datas */
import { MockedAPI } from "../hooks/hooksMockedApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";




import React, { PureComponent } from 'react';
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data = [
    {
      name: 'Page A',
      uv: 4000,
      pv: 2400,
      amt: 2400,
    },
    {
      name: 'Page B',
      uv: 3000,
      pv: 1398,
      amt: 2210,
    },
    {
      name: 'Page C',
      uv: 2000,
      pv: 9800,
      amt: 2290,
    },
    {
      name: 'Page D',
      uv: 2780,
      pv: 3908,
      amt: 2000,
    },
    {
      name: 'Page E',
      uv: 1890,
      pv: 4800,
      amt: 2181,
    },
    {
      name: 'Page F',
      uv: 2390,
      pv: 3800,
      amt: 2500,
    },
    {
      name: 'Page G',
      uv: 3490,
      pv: 4300,
      amt: 2100,
    },
];

const DailyActivitiesContainer = styled.div`
  position: relative;
  height: 100%;
  background: ${styleVar.neutral100};
`;

// export function DailyActivities({ userId }){

//     const api2 = new MockedAPI()
//     const dailyActivities = api2.getDailyActivitiesById(userId)

//     return(
//         <div>
//             <DailyActivitiesContainer>
//                 <LineChart width={500} height={300} data={data}>
//                     <XAxis dataKey="name"/>
//                     <YAxis/>
//                     <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
//                     <Line type="monotone" dataKey="uv" stroke="#8884d8" />
//                     <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
//                 </LineChart>

//             </DailyActivitiesContainer>
//         </div>
//     )
// }

export function DailyActivities({ userId }) {

    const api = new MockedAPI()
    const dailyActivities = api.getDailyActivitiesById(userId)
    const averageSession = api.getAverageSessionsById(userId)
    console.log(averageSession)

    return (
        <ResponsiveContainer width="835px" height="320px">
          <BarChart
            width={500}
            height={300}
            data={dailyActivities}
            margin={{
              top: 5,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="day" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar maxBarSize={7} dataKey="kilogram" fill={`${styleVar.neutral800}`} radius={[50, 50, 0, 0]}/>
            <Bar maxBarSize={7} dataKey="calories" fill={`${styleVar.primary500}`} radius={[50, 50, 0, 0]}/>
          </BarChart>
        </ResponsiveContainer>
      );

  }

function CustomTooltip({ active, payload }) {
    if (active && payload) {
      return (
        <TooltipContainer>
          <TooltipLine background={`${styleVar.neutral800}`}>
            {`${payload[0].value} kg`}
          </TooltipLine>
          <TooltipLine background={`${styleVar.primary500}`}>
            {`${payload[1].value} kCal`}
          </TooltipLine>
        </TooltipContainer>
      );
    }

    return null;
  }

  CustomTooltip.propTypes = {
    active: PropTypes.bool,
    payload: PropTypes.array,
  };


const DailyActivityChartContainer = styled.div`
position: relative;

height: 100%;

background: ${styleVar.neutral100};
`;

const DailyActivityChartTitle = styled.h2`
position: absolute;
top: 1.5rem;
left: 2rem;

margin: 0;

font-size: 1rem;
font-weight: 500;

@media (max-width: 1340px) {
  top: 1rem;
  left: 1.5rem;
}
`;

const DailyActivityChartLegend = styled.div`
display: flex;
position: absolute;
top: 1.5rem;
right: 2rem;

color: ${styleVar.neutral500};

@media (max-width: 1340px) {
  top: 1rem;
  right: 1.5rem;
}
`;

const LegendDetail = styled.p`
margin: 0 0 0 2rem;
`;

const ColorBullet = styled.span`
display: inline-block;

width: 0.5rem;
height: 0.5rem;
margin: 0 0.5rem 0 0;
border-radius: 50%;

background: ${(props) => props.background};
`;

const TooltipContainer = styled.div`
border: 2px solid rgba(255, 255, 255, 0.3);
`;

const TooltipLine = styled.p`
padding: 0.75rem;
margin: 0;

color: white;
font-size: 0.7rem;
font-weight: 500;

background: ${(props) => props.background};
`;
