import PropTypes from "prop-types";
import React from 'react';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts';

/** datas */
//import { MockedAPI } from "../hooks/useMockedApi";
import {
  getDefaultDailyActivity,
  useSportSeeApi,
} from "../hooks/useSportSeeApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";


export function DailyActivities({ userId }) {
  const { data, isLoading, error } = useSportSeeApi("daily-activity", userId );


  let dailyActivities = data;
  if (error || isLoading) {
    dailyActivities = getDefaultDailyActivity();
  }

    // const api = new MockedAPI()
    // const dailyActivities = api.getDailyActivitiesById(userId)

    //const averageSession = api.getAverageSessionsById(userId)

    return (
      <div></div>
    );
  }

function CustomTooltip({ active, payload }) {
    if (active && payload) {
      return (
        <TooltipContainer>
          <TooltipLine background={`${styleVar.neutral800}`}>
            {`${payload[0].value} kg`}
          </TooltipLine>
          <TooltipLine background={`${styleVar.primary500}`}>
            {`${payload[1].value} kCal`}
          </TooltipLine>
        </TooltipContainer>
      );
    }

    return null;
  }

  CustomTooltip.propTypes = {
    active: PropTypes.bool,
    payload: PropTypes.array,
  };


  const DailyActivityWrapper = styled.div`
  position: relative;

  height: 100%;

  background: ${styleVar.neutral100};
`;

const Title = styled.h2`
  position: absolute;
  top: 1.5rem;
  left: 2rem;

  margin: 0;

  font-size: 1rem;
  font-weight: 500;

  @media (max-width: 1340px) {
    top: 1rem;
    left: 1.5rem;
  }
`;

const LegendWrapper = styled.div`
  display: flex;
  position: absolute;
  top: 1.5rem;
  right: 2rem;

  color: ${styleVar.neutral500};

  @media (max-width: 1340px) {
    top: 1rem;
    right: 1.5rem;
  }
`;

const LegendDetail = styled.p`
  margin: 0 0 0 2rem;
`;

const Color = styled.span`
display: inline-block;

width: 0.5rem;
height: 0.5rem;
margin: 0 0.5rem 0 0;
border-radius: 50%;

background: ${(props) => props.background};
`;

const TooltipContainer = styled.div`
border: 2px solid rgba(255, 255, 255, 0.3);
`;

const TooltipLine = styled.p`
padding: 0.75rem;
margin: 0;

color: white;
font-size: 0.7rem;
font-weight: 500;

background: ${(props) => props.background};
`;
