import PropTypes from "prop-types";
import styled from "styled-components";

import { styleVar } from "../utils/style/styleVariables";
import calorieIcon from "../assets/icon-calorie.png";
import proteinIcon from "../assets/icon-protein.png";
import carbohydrateIcon from "../assets/icon-carbohydrate.png";
import lipidIcon from "../assets/icon-lipid.png";

import {

  useSportSeeApi,
  getDefaultKeyData,
} from "../hooks/useSportSeeApi";


export function CaloriesCard({ userId }) {
  const { data, isLoading, error } = useSportSeeApi("user", userId );

  let calorieInfos = data;
  if (error || isLoading) {
    calorieInfos = getDefaultKeyData();
  }

  return (

    <div>
      <WrapperCard>
        <img src={calorieIcon} alt="Logo calorie" width="60" height="60" />

        <InfoCardData>
          <InfoCardMeasure data-testid="card-measure">
          {calorieInfos.calorieCount}kCal
          </InfoCardMeasure>
          <NameMeasure>Calories</NameMeasure>
        </InfoCardData>

      </WrapperCard>
      <WrapperCard>
        <img src={proteinIcon} alt="Logo proteine" width="60" height="60" />

        <InfoCardData>
          <InfoCardMeasure data-testid="card-measure">
          {calorieInfos.proteinCount}g
          </InfoCardMeasure>
          <NameMeasure>Prot√©ines</NameMeasure>
        </InfoCardData>

      </WrapperCard>
      <WrapperCard>
        <img src={carbohydrateIcon} alt="Logo glucide" width="60" height="60" />

        <InfoCardData>
          <InfoCardMeasure data-testid="card-measure">
          {calorieInfos.carbohydrateCount}g
          </InfoCardMeasure>
          <NameMeasure>Glucides</NameMeasure>
        </InfoCardData>

      </WrapperCard>
      <WrapperCard>
        <img src={lipidIcon} alt="Logo lipide" width="60" height="60" />

        <InfoCardData>
          <InfoCardMeasure data-testid="card-measure">
          {calorieInfos.lipidCount}g
          </InfoCardMeasure>
          <NameMeasure>Lipides</NameMeasure>
        </InfoCardData>

      </WrapperCard>
    </div>
  );
}


const WrapperCard = styled.div`
  background: ${styleVar.neutral100}; 
  border-radius: 5px;
  display: flex;
  margin: 0 0 20px 0;
  padding: 2rem;
  text-align: left;
  @media (max-width: 1340px) {
    padding: 1.75rem 1.25rem;
  }
`;

const InfoCardData = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;

  padding: 0 0 0 1.5rem;

  @media (max-width: 1340px) {
    padding: 0 0 0 1rem;
  }
`;

const InfoCardMeasure = styled.div`
  margin: 0.125rem 0;

  font-size: 1.2rem;
  font-weight: 700;

  @media (max-width: 1340px) {
    font-size: 1.1rem;
  }
`;

const NameMeasure = styled.div`
  font-size: 12px
`;
