import PropTypes from "prop-types";
import styled from "styled-components";

import { styleVar } from "../utils/style/styleVariables";
import calorieIcon from "../assets/icon-calorie.png";
import carbohydrateIcon from "../assets/icon-carbohydrate.png";
import lipidIcon from "../assets/icon-lipid.png";
import proteinIcon from "../assets/icon-protein.png";

import {

  useSportSeeApi,
  getDefaultKeyData,
  getDefaultDailyActivity
} from "../hooks/useSportSeeApi";




export function CaloriesCard({ userId }) {
  const { data, isLoading, error } = useSportSeeApi("user", userId );


  let calorieInfos = data;
  if (error || isLoading) {
    calorieInfos = getDefaultDailyActivity();
  }

  console.log(calorieInfos)
  return (
    <InfoCardContainer>
      <img width="60" height="60" />

      <InfoCardData>
        <InfoCardMeasure data-testid="card-measure">

        </InfoCardMeasure>

        <InfoCardType data-testid="card-type">{calorieInfos.caloriC}</InfoCardType>
      </InfoCardData>
    </InfoCardContainer>
  );
}

// CaloriesCard.propTypes = {
//   type: PropTypes.string.isRequired,
//   value: PropTypes.number.isRequired,
// };

const InfoCardContainer = styled.div`
  display: flex;

  padding: 2rem;

  text-align: left;

  background: ${styleVar.neutral100};

  @media (max-width: 1340px) {
    padding: 1.75rem 1.25rem;
  }
`;

const InfoCardData = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;

  padding: 0 0 0 1.5rem;

  @media (max-width: 1340px) {
    padding: 0 0 0 1rem;
  }
`;

const InfoCardMeasure = styled.div`
  margin: 0.125rem 0;

  font-size: 1.2rem;
  font-weight: 700;

  @media (max-width: 1340px) {
    font-size: 1.1rem;
  }
`;

const InfoCardType = styled.div`
  margin: 0.125rem 0;

  color: ${styleVar.neutral500};
`;
