import { useState, useEffect } from 'react'

const BASE_URL = "http://localhost:3000";

export function useFetchApi(userId, service) {
    const [data, setData] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(false);

    const endpoint = getEndpoint(userId, service);
    const url = `${BASE_URL}/${endpoint2}`;
    useEffect(() => {
        setIsLoading(true);

        fetch(BASE_URL)
            .then(res =>res.json())
            .then((data) => {
                console.log(data)
                setData()


                for(let item of data){
                    setData(item, service);
                }
            })
            .catch ((err) =>{
                console.log(err)
                setError(true)
            })
            }, [])
    return { data, isLoading, error };
}

function getEndpoint(userId, service){
    const endpoints ={
        "user" : `user/${userId}`, 
        "daily-activity" : `user/${userId}/activity`, 
        "average-sessions": `user/${userId}/average-sessions`,
        "performance" : `user/${userId}/performance`
    }
    let endpoint;

    for(let i in endpoints){
        let value = endpoints[i];
        if(i === service){
            endpoint = value;
        }
    }
    return endpoint;
}
