import { useEffect, useState } from "react";

import { MockedAPI } from "../hooks/useMockedApi";
const BASE_URL = "http://localhost:3000";

const ACTIVITY_BY_KIND = {
  1: "Cardio",
  2: "Energie",
  3: "Endurance",
  4: "Force",
  5: "Vitesse",
  6: "Intensité",
};


export function useSportSeeApi(service, userId) {
    /** ici le param service sera par ex "daily-activity" 
     * (liste ds fonction getEndpointByService) 
     */
  const [data, setData] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);

  const endpoints ={
    "user" : `user/${userId}`, 
    "daily-activity" : `user/${userId}/activity`, 
    "average-sessions": `user/${userId}/average-sessions`,
    "performance" : `user/${userId}/performance`,
    "score" : `user/${userId}`
  }

  //const endpoint = getEndpointByService(service, userId);
  let endpoint;
  useEffect(() => {

    for(let i in endpoints){
        let value = endpoints[i];
        if(i === service){
            endpoint = value;
        }
    }

    if (!endpoint) {
      /** a voir comment faire ici pr return les données mocked */
      // const mockedApi = new MockedAPI();
      // return mockedApi;
      return;
    }

    setIsLoading(true);

    fetch(`${BASE_URL}/${endpoint}`)
        .then(res =>res.json())
        .then((data) => {
            const extractedData = getDataByService(data, service);
            setData(extractedData)
            setIsLoading(false)
        })
        .catch ((err) =>{
            console.log(err)
            setError(true)
        })

  }, [service, userId, endpoint]);

  return { data, isLoading, error };
}


