      case "user":
        return getKeyData(data);

      case "score":
        return getScore(data);

      default:
        console.error(
          `getDataByService error: service "${service}" is not defined.`
        );
        return;
    }
  }

  console.error("getDataByService error: no data to process.");
  return;
}


