import PropTypes from "prop-types";
import React from 'react';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts';

/** datas */
//import { MockedAPI } from "../hooks/useMockedApi";
import {
  getDefaultDailyActivity,
  useSportSeeApi,
} from "../hooks/useSportSeeApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";


const DailyActivitiesWrapper = styled.div`
  background: ${styleVar.neutral100};
  border-radius: 5px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.0212249);

  position: relative;
`;

const Title = styled.h3`
  position: absolute;
  height: 24px;
  left: 3.83%;
  right: 80%;
  top: calc(50% - 24px/2 - 124px);

  font-size: 15px;
  line-height: 24px;

  /* identical to box height, or 160% */
  color: #20253A;
`;

const LegendWrapper = styled.div`
  display: flex;
  position: absolute;
  top: 1.5rem;
  right: 2rem;

  color: ${styleVar.neutral500};

  @media (max-width: 1340px) {
    top: 1rem;
    right: 1.5rem;
  }
`;

const LegendDetail = styled.p`
  margin: 0 0 0 2rem;
`;


export function DailyActivities({ userId }) {
  const { data, isLoading, error } = useSportSeeApi("daily-activity", userId );

  let dailyActivities = data;
  if (error || isLoading) {
    dailyActivities = getDefaultDailyActivity();
  }

    // const api = new MockedAPI()
    // const dailyActivities = api.getDailyActivitiesById(userId)

    //const averageSession = api.getAverageSessionsById(userId)

    return (
      <DailyActivityChartContainer>
      <DailyActivityChartTitle>Activité quotidienne</DailyActivityChartTitle>

      <DailyActivityChartLegend>
        <LegendDetail>
          <ColorBullet background={`${styleVar.neutral800}`}></ColorBullet>
          Poids (kg)
        </LegendDetail>
        <LegendDetail>
          <ColorBullet background={`${styleVar.primary500}`}></ColorBullet>
          Calories brûlées (kCal)
        </LegendDetail>
      </DailyActivityChartLegend>

      <ResponsiveContainer width="100%" height="100%">
        <BarChart
          data={dailActivity}
          margin={{ top: 80, right: 48, bottom: 32, left: 48 }}
          barGap={8}
          barCategoryGap="35%"
        >
          <CartesianGrid
            strokeDasharray="3 3"
            vertical={false}
            stroke={`${styleVar.neutral200}`}
          />
          <XAxis
            dataKey="day"
            dy={16}
            padding={{ left: -48, right: -48 }}
            stroke={`${styleVar.neutral400}`}
            tickLine={false}
            tick={{ fontSize: 14, fontWeight: 500 }}
          />
          <YAxis
            yAxisId="kg"
            dataKey="kilogram"
            domain={["dataMin - 1", "dataMax + 2"]}
            allowDecimals={false}
            dx={48}
            orientation="right"
            stroke={`${styleVar.neutral400}`}
            axisLine={false}
            tickLine={false}
          />
          <YAxis
            yAxisId="cal"
            dataKey="calories"
            domain={[0, "dataMax + 50"]}
            hide={true}
          />
          <Bar
            yAxisId="kg"
            dataKey="kilogram"
            maxBarSize={8}
            fill={`${styleVar.neutral800}`}
            radius={[50, 50, 0, 0]}
          />
          <Bar
            yAxisId="cal"
            dataKey="calories"
            maxBarSize={8}
            fill={`${styleVar.primary500}`}
            radius={[50, 50, 0, 0]}
          />
          <Tooltip
            content={<CustomTooltip />}
            cursor={{
              fill: "rgba(0, 0, 0, 0.1)",
            }}
          />
        </BarChart>
      </ResponsiveContainer>
    </DailyActivityChartContainer>
      // <DailyActivitiesWrapper>
      //   <Title>Activité quotidienne</Title>
      //   <LegendWrapper>
      //     <LegendDetail>Poids (kg)</LegendDetail>
      //     <LegendDetail>Calories brûlées (kCal)</LegendDetail>
      //   </LegendWrapper>
      //     <ResponsiveContainer width="100%" height="100%">
      //       <BarChart
      //         width={500}
      //         height={300}
      //         data={dailyActivities}
      //         margin={{
      //           top: 5,
      //           right: 30,
      //           left: 20,
      //           bottom: 5,
      //         }}
      //       >
      //         <CartesianGrid vertical={false} strokeDasharray="2" />
      //         <XAxis dataKey="day" />
      //         <YAxis dataKey="kilogram" orientation="right" domain={["dataMin - 1", "dataMax + 1"]}/>
      //         <Tooltip />
      //         {/* <Legend /> */}
      //         <Bar dataKey="kilogram" maxBarSize={7} fill={`${styleVar.neutral800}`}  radius={[50, 50, 0, 0]}/>
      //         <Bar dataKey="calories" maxBarSize={7} fill={`${styleVar.primary500}`} radius={[50, 50, 0, 0]}/>
      //       </BarChart>
      //     </ResponsiveContainer>

      //   </DailyActivitiesWrapper>
      );

  }

function CustomTooltip({ active, payload }) {
    if (active && payload) {
      return (
        <TooltipContainer>
          <TooltipLine background={`${styleVar.neutral800}`}>
            {`${payload[0].value} kg`}
          </TooltipLine>
          <TooltipLine background={`${styleVar.primary500}`}>
            {`${payload[1].value} kCal`}
          </TooltipLine>
        </TooltipContainer>
      );
    }

    return null;
  }

  CustomTooltip.propTypes = {
    active: PropTypes.bool,
    payload: PropTypes.array,
  };


  const DailyActivityChartContainer = styled.div`
  position: relative;

  height: 100%;

  background: ${styleVar.neutral100};
`;

const DailyActivityChartTitle = styled.h2`
  position: absolute;
  top: 1.5rem;
  left: 2rem;

  margin: 0;

  font-size: 1rem;
  font-weight: 500;

  @media (max-width: 1340px) {
    top: 1rem;
    left: 1.5rem;
  }
`;

const DailyActivityChartLegend = styled.div`
  display: flex;
  position: absolute;
  top: 1.5rem;
  right: 2rem;

  color: ${styleVar.neutral500};

  @media (max-width: 1340px) {
    top: 1rem;
    right: 1.5rem;
  }
`;

const ColorBullet = styled.span`
display: inline-block;

width: 0.5rem;
height: 0.5rem;
margin: 0 0.5rem 0 0;
border-radius: 50%;

background: ${(props) => props.background};
`;

const TooltipContainer = styled.div`
border: 2px solid rgba(255, 255, 255, 0.3);
`;

const TooltipLine = styled.p`
padding: 0.75rem;
margin: 0;

color: white;
font-size: 0.7rem;
font-weight: 500;

background: ${(props) => props.background};
`;
