import PropTypes from "prop-types";
import React from 'react';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts';

/** datas */
import { MockedAPI } from "../hooks/hooksMockedApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";

const DailyActivitiesWrapper = styled.div`
  background: ${styleVar.neutral100};
  border-radius: 5px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.0212249);
  height: 320px;
  position: relative;
  width: 835px;
`;


const DailyActivitiesTitle = styled.h3`
  position: absolute;
  height: 24px;
  left: 3.83%;
  right: 80%;
  top: calc(50% - 24px/2 - 124px);

  font-size: 15px;
  line-height: 24px;

  /* identical to box height, or 160% */
  color: #20253A;
`;

const ChartWrapper = styled.div`
position: absolute;
left: 5.15%;
right: 10.78%;
top: 35.16%;
bottom: 19.53%;

`;
/**position: absolute;
  top: 1.5rem;
  left: 2rem;

  margin: 0;

  font-size: 1rem;
  font-weight: 500;

  @media (max-width: 1340px) {
    top: 1rem;
    left: 1.5rem;
  } */
export function DailyActivities({ userId }) {

    const api = new MockedAPI()
    const dailyActivities = api.getDailyActivitiesById(userId)
    //const averageSession = api.getAverageSessionsById(userId)


    return (
      <DailyActivitiesWrapper width="862" height="320">
       < DailyActivitiesTitle>Activit√© quotidienne</DailyActivitiesTitle>
        <ChartWrapper>
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              width={500}
              height={300}
              data={dailyActivities}
              margin={{
                top: 5,
                right: 30,
                left: 20,
                bottom: 5,
              }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="day" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar maxBarSize={7} dataKey="kilogram" fill={`${styleVar.neutral800}`} radius={[50, 50, 0, 0]}/>
              <Bar maxBarSize={7} dataKey="calories" fill={`${styleVar.primary500}`} radius={[50, 50, 0, 0]}/>
            </BarChart>
          </ResponsiveContainer>
        </ChartWrapper>

      </DailyActivitiesWrapper>

      );

  }

// function CustomTooltip({ active, payload }) {
//     if (active && payload) {
//       return (
//         <TooltipContainer>
//           <TooltipLine background={`${styleVar.neutral800}`}>
//             {`${payload[0].value} kg`}
//           </TooltipLine>
//           <TooltipLine background={`${styleVar.primary500}`}>
//             {`${payload[1].value} kCal`}
//           </TooltipLine>
//         </TooltipContainer>
//       );
//     }

//     return null;
//   }

  // CustomTooltip.propTypes = {
  //   active: PropTypes.bool,
  //   payload: PropTypes.array,
  // };





const DailyActivityLegend = styled.div`
display: flex;
position: absolute;
top: 1.5rem;
right: 2rem;

color: ${styleVar.neutral500};

@media (max-width: 1340px) {
  top: 1rem;
  right: 1.5rem;
}
`;

const LegendDetail = styled.p`
margin: 0 0 0 2rem;
`;

const ColorBullet = styled.span`
display: inline-block;

width: 0.5rem;
height: 0.5rem;
margin: 0 0.5rem 0 0;
border-radius: 50%;

background: ${(props) => props.background};
`;

const TooltipContainer = styled.div`
border: 2px solid rgba(255, 255, 255, 0.3);
`;

const TooltipLine = styled.p`
padding: 0.75rem;
margin: 0;

color: white;
font-size: 0.7rem;
font-weight: 500;

background: ${(props) => props.background};
`;
