import PropTypes from "prop-types";

/** Recharts */
import { 
  Cell, 
  Pie, 
  PieChart, 
  ResponsiveContainer 
} from "recharts";

/** Styles */
import styled from "styled-components";

/** Datas */
import { FacadePattern } from "../hooks/FacadePattern";
// import { MockedAPI } from "../hooks/useMockedApi";
// import { useSportSeeApi } from "../hooks/useSportSeeApi";

import { color } from "../utils/style/styleVariables";

export function Score({ userId }) {

  const data = FacadePattern(userId, "score");
  const score = data.getPerformancesById(userId);
  // const { data, isLoading, error } = useSportSeeApi("score", userId);
  // let score = data;

  // if (error || isLoading) {
  //   const api = new MockedAPI()
  //   score = api.getScoreById(userId)
  // }

  const pieData = [
    { name: "completed", value: score, fillColor: `${color.primary500}` },
    { name: "not-completed", value: 1 - score, fillColor: "transparent" },
  ];

  return (
    <ScoreContainer>
      <ScoreTitle>Score</ScoreTitle>

      <ResponsiveContainer width="100%" height="100%">
        <PieChart width={160} height={160}>
          <Pie
            data={pieData}
            dataKey="value"
            innerRadius={70}
            outerRadius={80}
            startAngle={90}
            endAngle={450}
          >
            {pieData.map((entry, index) => (
              <Cell
                key={`cell-${index}`}
                fill={entry.fillColor}
                cornerRadius="50%"
              />
            ))}
          </Pie>
        </PieChart>
      </ResponsiveContainer>

      <ScoreLabel>
        <ScoreValue>{`${100 * score}%`}</ScoreValue>
        <br />
        de votre
        <br />
        objectif
      </ScoreLabel>
    </ScoreContainer>
  );
}

Score.propTypes = {
  userId: PropTypes.number.isRequired,
};

const ScoreContainer = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;

  background: ${color.neutral100};
`;

const ScoreTitle = styled.h2`
  position: absolute;
  top: 1.5rem;
  left: 2rem;

  margin: 0;

  font-size: 1rem;
  font-weight: 500;

  @media (max-width: 1340px) {
    top: 1rem;
    left: 1rem;
  }
`;

const ScoreLabel = styled.p`
  position: absolute;

  font-size: 1rem;
  text-align: center;
`;

const ScoreValue = styled.span`
  font-size: 1.5rem;
  font-weight: 700;
  text-align: center;
`;
