import PropTypes from "prop-types";

import {
    Bar,
    BarChart,
    CartesianGrid,
    ResponsiveContainer,
    Tooltip,
    XAxis,
    YAxis,
  } from "recharts";
/** datas */
import { MockedAPI } from "../hooks/hooksMockedApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";

const DailyActivitiesContainer = styled.div`
  position: relative;
  height: 100%;
  background: ${styleVar.neutral100};
`;

export function DailyActivities({ userId }){

    const api2 = new MockedAPI()
    const dailyActivities = api2.getDailyActivitiesById(userId)

    return(
        <div>
            <DailyActivitiesContainer>
                <ResponsiveContainer width="100%" height="100%">
                    <BarChart
                    data={dailyActivities}
                    margin={{ top: 80, right: 48, bottom: 32, left: 48 }}
                    barGap={8}
                    barCategoryGap="35%"
                    >
                    <CartesianGrid
                        strokeDasharray="3 3"
                        vertical={false}
                        stroke={`${styleVar.neutral200}`}
                    />
                    <XAxis
                        dataKey="day"
                        dy={16}
                        padding={{ left: -48, right: -48 }}
                        stroke={`${styleVar.neutral400}`}
                        tickLine={false}
                        tick={{ fontSize: 14, fontWeight: 500 }}
                    />
                    <YAxis
                        yAxisId="kg"
                        dataKey="kilogram"
                        domain={["dataMin - 1", "dataMax + 2"]}
                        allowDecimals={false}
                        dx={48}
                        orientation="right"
                        stroke={`${styleVar.neutral400}`}
                        axisLine={false}
                        tickLine={false}
                    />
                    <YAxis
                        yAxisId="cal"
                        dataKey="calories"
                        domain={[0, "dataMax + 50"]}
                        hide={true}
                    />
                    <Bar
                        yAxisId="kg"
                        dataKey="kilogram"
                        maxBarSize={8}
                        fill={`${styleVar.neutral800}`}
                        radius={[50, 50, 0, 0]}
                    />
                    <Bar
                        yAxisId="cal"
                        dataKey="calories"
                        maxBarSize={8}
                        fill={`${styleVar.primary500}`}
                        radius={[50, 50, 0, 0]}
                    />
                    <Tooltip

                        cursor={{
                        fill: "rgba(0, 0, 0, 0.1)",
                        }}
                    />
                    </BarChart>
                </ResponsiveContainer>

            </DailyActivitiesContainer>
        </div>
    )
}


function CustomTooltip({ active, payload }) {
    if (active && payload) {
      return (
        <TooltipContainer>
          <TooltipLine background={`${styleVar.neutral800}`}>
            {`${payload[0].value} kg`}
          </TooltipLine>
          <TooltipLine background={`${styleVar.primary500}`}>
            {`${payload[1].value} kCal`}
          </TooltipLine>
        </TooltipContainer>
      );
    }

    return null;
  }

  CustomTooltip.propTypes = {
    active: PropTypes.bool,
    payload: PropTypes.array,
  };


const DailyActivityChartContainer = styled.div`
position: relative;

height: 100%;

background: ${styleVar.neutral100};
`;

const DailyActivityChartTitle = styled.h2`
position: absolute;
top: 1.5rem;
left: 2rem;

margin: 0;

font-size: 1rem;
font-weight: 500;

@media (max-width: 1340px) {
  top: 1rem;
  left: 1.5rem;
}
`;

const DailyActivityChartLegend = styled.div`
display: flex;
position: absolute;
top: 1.5rem;
right: 2rem;

color: ${styleVar.neutral500};

@media (max-width: 1340px) {
  top: 1rem;
  right: 1.5rem;
}
`;

const LegendDetail = styled.p`
margin: 0 0 0 2rem;
`;

const ColorBullet = styled.span`
display: inline-block;

width: 0.5rem;
height: 0.5rem;
margin: 0 0.5rem 0 0;
border-radius: 50%;

background: ${(props) => props.background};
`;

const TooltipContainer = styled.div`
border: 2px solid rgba(255, 255, 255, 0.3);
`;

const TooltipLine = styled.p`
padding: 0.75rem;
margin: 0;

color: white;
font-size: 0.7rem;
font-weight: 500;

background: ${(props) => props.background};
`;
