import PropTypes from "prop-types";
import React from 'react';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts';

/** datas */
import { MockedAPI } from "../hooks/hooksMockedApi";
import {
  //getDefaultDailyActivity,
  useSportSeeAPI,
} from "../hooks/useSportSeeApi";
import {
  //getDefaultDailyActivity,
  useFetchApi,
} from "../hooks/useFetchApi";

/** styles */
import styled from "styled-components";
import { styleVar } from "../utils/style/styleVariables";

const DailyActivitiesWrapper = styled.div`
  background: ${styleVar.neutral100};
  border-radius: 5px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.0212249);
  height: 320px;
  position: relative;
  width: 835px;
`;

const Title = styled.h3`
  position: absolute;
  height: 24px;
  left: 3.83%;
  right: 80%;
  top: calc(50% - 24px/2 - 124px);

  font-size: 15px;
  line-height: 24px;

  /* identical to box height, or 160% */
  color: #20253A;
`;

const LegendWrapper = styled.div`
  display: flex;
  position: absolute;
  top: 1.5rem;
  right: 2rem;

  color: ${styleVar.neutral500};

  @media (max-width: 1340px) {
    top: 1rem;
    right: 1.5rem;
  }
`;

const LegendDetail = styled.p`
  margin: 0 0 0 2rem;
`;

const Chart = styled.div`
  position: absolute;
  left: 5.15%;
  right: 10.78%;
  top: 35.16%;
  bottom: 19.53%;

`;


export function DailyActivities({ userId }) {
  const { data, isLoading, error } = useSporteeApi( userId, "daily-activity");

  let dailyActivities = data;
  console.log(data)
  // if (error || isLoading) {
  //   dailyActivities = getDefaultDailyActivity();
  // }

    // const api = new MockedAPI()
    // const dailyActivities = api.getDailyActivitiesById(userId)

    //const averageSession = api.getAverageSessionsById(userId)

    return (
      <DailyActivitiesWrapper>
        <Title>Activité quotidienne</Title>
        <LegendWrapper>
          <LegendDetail>Poids (kg)</LegendDetail>
          <LegendDetail>Calories brûlées (kCal)</LegendDetail>
        </LegendWrapper>

        <Chart>
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              width={500}
              height={300}
              data={dailyActivities}
              margin={{
                top: 5,
                right: 30,
                left: 20,
                bottom: 5,
              }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="day" />
              <YAxis dataKey="kilogram" orientation="right"/>
              <Tooltip />
              <Legend />
              <Bar dataKey="kilogram" maxBarSize={7} fill={`${styleVar.neutral800}`} radius={[50, 50, 0, 0]}/>
              <Bar dataKey="calories" maxBarSize={7} fill={`${styleVar.primary500}`} radius={[50, 50, 0, 0]}/>
            </BarChart>
          </ResponsiveContainer>
        </Chart>

      </DailyActivitiesWrapper>

      );

  }

