display: inline-block;

width: 0.5rem;
height: 0.5rem;
margin: 0 0.5rem 0 0;
border-radius: 50%;

background: ${(props) => props.background};
`;

const TooltipContainer = styled.div`
border: 2px solid rgba(255, 255, 255, 0.3);
`;

const TooltipLine = styled.p`
padding: 0.75rem;
margin: 0;

color: white;
font-size: 0.7rem;
font-weight: 500;

background: ${(props) => props.background};
`;
